mapper.select:
SELECT * FROM ( SELECT a.*, b.name region_name, IFNULL(c.warn_level, 0) warn_level, IFNULL(d.device_num, 0) device_num FROM company a LEFT JOIN sys_region b ON a.region=b.id LEFT JOIN (SELECT cid, MAX(warn_level) warn_level FROM alert GROUP BY cid) c ON a.id=c.cid LEFT JOIN (SELECT cid, COUNT(cid) device_num FROM device GROUP BY cid) d ON a.id=d.cid
a.id=#{id}
AND a.region >= #{min}
AND a.region <= #{max}
AND a.type=#{type}
AND a.`name` LIKE concat(concat('%',#{name}),'%')
) e
`warn_level`=#{warnLevel}
order by warn_level desc

SELECT 
	a.id,
	a.name
FROM 
	company a

	
		 AND a.region >= #{min} 
	
	
		 AND a.region <= #{max} 
	
	
			AND a.type=#{type}

SELECT warn_level, count(*) num FROM( SELECT a.*, IFNULL(b.warn_level, 0) warn_level FROM company a LEFT JOIN (SELECT cid, MAX(warn_level) warn_level FROM alert GROUP BY cid) b ON a.`id`=b.cid
a.id=#{cid}
AND a.region >= #{min}
AND a.region <= #{max}
) c GROUP BY warn_level
