mapper.select:
SELECT a.`id`, a.`uid`, a.`cid`, a.`state`, b.`region`, b.`name` cname, b.`type` ctype FROM employee a LEFT JOIN company b ON a.`cid`=b.`id`
a.id=#{id}
a.uid=#{uid}
AND b.type=#{type}
AND a.cid=#{cid}
AND b.`name` LIKE concat(concat('%',#{name}),'%')

SELECT warn_level, SUM(num) num FROM (SELECT b.num, b.warn_level FROM company a LEFT JOIN (SELECT cid, warn_level, COUNT(*) num FROM alert GROUP BY cid, warn_level ) b ON a.`id`=b.`cid`
a.id=#{cid}
AND a.region >= #{min}
AND a.region <= #{max}
) c GROUP BY warn_level
