mapper.select:
SELECT c.*, IFNULL(d.expire_time, 0) cer_expire FROM operator c LEFT JOIN ( SELECT * FROM operator_cert WHERE id IN ( SELECT SUBSTRING_INDEX(GROUP_CONCAT(id ORDER BY expire_time DESC), ',', 1) id FROM operator_cert GROUP BY operator_id) )d ON c.id=d.operator_id
c.sex=#{sex}
AND c.id=#{id}
AND c.cid=#{cid}
AND c.`name` LIKE concat(concat('%',#{name}),'%')
